import React, {useState} from 'react';
import {Link} from 'react-router-dom';

const Register = () => {
    const [form, setForm] = useState({
        email: '',
        username: '',
        password: ''
    });

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value});
    }

    const onClickHandler = (event) => {
        event.preventDefault();

    }

    return (
        <>
            <h3>Create an account</h3>
            <form>
                <label htmlFor='email'>
                    <span>email address: </span>
                    <input type='text' value={form.email} name='email' onChange={handleFormChange} />
                </label>
                <label htmlFor='reguser'>
                    <span>username: </span>
                    <input type='text' value={form.username} name='username' onChange={handleFormChange} />
                    <em>Create a username at least 6 characters long.</em>
                </label>
                <label htmlFor='regpwd'>
                    <span>password: </span>
                    <input type='password' name='password' onChange={handleFormChange} />
                    <em>(Create a password at least 8 characters long, including uppercase and lowercase letters, numbers, and a special character (e.g., $ ! @ #).</em>
                </label>
                <input type='submit' value='Submit' onClick={onClickHandler} />
            </form>
            <p>Already have an account? Click <Link to='/account/login'>here</Link> to log in.</p>
        </>
    );
}

export default Register;
