import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import {loginUser} from '../api';

const Login = () => {
    const [form, setForm] = useState({
        username: '',
        password: ''
    })

    const [errorMessage, setErrorMessage] = useState();

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value.trim()});
    }

    const onClickHandler = async (event) => {
        event.preventDefault();
        const {username, password} = form;
        try {
            const {user, message} = await loginUser(username, password);
            alert(message);
            setUser({username: user[username], loggedIn: true});
            setForm({username: '', password: ''});            
        } catch (error) {
            setErrorMessage('Something\'s not right! Please check and try again.')
            throw error;
        }


    }

    return (
        <>
            <h3>Login</h3>
            <form>
                <label>username: </label>
                <input type='text' value={form.username} name='username' onChange={handleFormChange} />
                <label>password: </label>
                <input type='text' value={form.password} name='password' onChange={handleFormChange} />
                {errorMessage && (
                    <p className='error-message'>{errorMessage}</p>
                )}
                <input type='submit' value='Log in' onClick={onClickHandler} />
            </form>
            <p>Need an account? Click <Link to='/account/register'>here</Link> to register.</p>
        </>
    );
}

export default Login;
