import React, {useEffect, useState} from 'react';
import { fetchAllPublicRoutines } from '../api';

const Routines = () => {
    const [routineList, setRoutineList] = useState([]);

    const fetchData = async () => {
        try {
            const results = await fetchAllPublicRoutines();
            console.log(results);
        } catch (error) {
            console.error(error);
        }
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            <h2>All routines:</h2>
            <div id='routines'>
                {routineList.length > 0 && routineList.map(({name, goal, creatorName, activities, id}) => {
                    return <div key={id} className='routine'>
                        <h3>Routine: <span className='routine-name'>{name}</span></h3>
                        <p>{goal}</p>
                        <p>Activities: </p>
                        <p>Created by: {creatorName}</p>

                    </div>
                })}
            </div>
        </>
    )
}

export default Routines;
