import React, {useState, useEffect} from 'react';

import {fetchAllActivities, addActivityToRoutine} from '../api';

const MyRoutinesActivitiesForm = ({errorMessage, setErrorMessage, id}) => {
    const [form, setForm] = useState({
        name: '',
        goal: '',
        isPublic: false
    })

    const [activityList, setActivityList] = useState([]);

    const [selectedActivity, setSelectedActivity] = useState({
        routineId: id,
        activityId: 1,
        count: 1,
        duration: 1,
    });

    const [pendingActivities, setPendingActivities] = useState([]);

    const fetchData = async () => {
        try {
            const results = await fetchAllActivities();
            setActivityList(results);
        } catch (error) {
            throw error;
        };
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleSelectChange = (event) => {
        const selectedIndex = event.target.options.selectedIndex;
        const activityId = Number(event.target.options[selectedIndex].getAttribute('data-key'));
        setSelectedActivity({...selectedActivity, activityId: activityId});
    }

    const handleNumberChange = (event) => {
        setSelectedActivity({...selectedActivity, [event.target.name]: Number(event.target.value)});
    }

    // const handleAddActivity = async (event) => {
    //     event.preventDefault();
    //     const {routineId, activityId, count, duration} = selectedActivity;
    //     try {
    //         await addActivityToRoutine(routineId, activityId, count, duration);
    //     } catch (error) {
    //         console.log(error);
    //     }
    // }

    const handleAddActivity = (event) => {
        event.preventDefault();
        const [activity] = activityList.filter(activity => selectedActivity.activityId === activity.id);
        const activityName = activity.name;
        console.log(activityName)
        setPendingActivities([...pendingActivities, selectedActivity])
    }

    return (
        <>
            <select name='activities' onChange={handleSelectChange}>
                {activityList ? activityList.map(({id, name}) => {
                    return <option key={id} data-key={id} value={name}>{name}</option>
                }) : null}
            </select>
            {errorMessage && (
                <p className='error-message'>{errorMessage}</p>
            )}
            <label>
                <span>Count: </span>
                <input type='number' name='count' value={selectedActivity.count} onChange={handleNumberChange} />
            </label>
            <label>
                <span>Duration (minutes): </span>
                <input type='number' name='duration' value={selectedActivity.duration} onChange={handleNumberChange} />
            </label>
            <button onClick={handleAddActivity}>Add activity</button>
            <ul>
                {pendingActivities.length > 0 && pendingActivities.map((activity) => {
                    return <li>{}</li>
                })}
            </ul>
        </>
    )
}

export default MyRoutinesActivitiesForm;
