import React from 'react';

import {
    Route,
    Switch,
    Redirect
} from 'react-router-dom';

import {
    Login,
    Register,
    User
} from './AccountSub';

const Account = ({user, setUser}) => {
    return (
        <div className="account">
            {/* Figure out how to restructure with ternaries so user shows when logged in */}
            <Switch>
                <Route path='/user'>
                    {!user ? <Redirect to='/login' /> : <User user={user} />}
                </Route>
                <Route path={`/login`}>
                    <Login user={user} />
                </Route>
                <Route path={`/register`}>
                    <Register setUser={setUser} />
                </Route>
            </Switch>
        </div>
    );
}

export default Account;
