import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser} from '../api';

import {
    MyRoutinesForm,
    MyRoutinesItem
} from './index';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }

    function checkRoutineList(name) {
        const myRoutinesListLowercase = myRoutinesList.map((routine) => {
            const routineName = routine.name.toLowerCase();
            return routineName;
        });
        const nameLowercase = name.toLowerCase();
        return myRoutinesListLowercase.includes(nameLowercase);
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            <p>Create and customize your personal routines here! It's up to you whether you make them public or keep them private. Be sure to add some activities once you've created a routine.</p>
            <div id='my-routines'>
                <MyRoutinesForm
                    fetchData={fetchData}
                    myRoutinesList={myRoutinesList}
                    checkRoutineList={checkRoutineList}
            />
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map(({name, goal, creatorName, activities, isPublic, id}) => {
                        return <MyRoutinesItem
                            key={id}
                            fetchData={fetchData}
                            checkRoutineList={checkRoutineList}
                            myRoutinesList={myRoutinesList}
                            name={name}
                            goal={goal}
                            creatorName={creatorName}
                            activities={activities}
                            isPublic={isPublic}
                            id={id}
                        />
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
