import React, {useState} from 'react';

import {createRoutine} from '../api';

const MyRoutinesItem = ({errorMessage, setErrorMessage, handleFormChange, handleOptionChange, handleDelete, name, goal, creatorName, activities, isPublic, id}) => {
    const [form, setForm] = useState({
        name: name,
        goal: goal,
        isPublic: isPublic
    })
    const [toggle, setToggle] = useState(false)

    const handleEditToggle = () => {
        setToggle(!toggle);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage('');
        const {name, goal, isPublic} = form;
        if (checkRoutineList(name)) {
            setErrorMessage('This routine already exists! Please choose another name.');
            return;
        }
        try {
            await createRoutine(name, goal, isPublic);
            setForm({name: '', goal: '', isPublic: false});
            fetchData();  
        } catch (error) {
            console.error(error);
        }
    }

    {toggle ? (
        <>
            <div key={id} className='routine'>
                <span className='material-icons' onClick={handleEditToggle}>edit</span>
                <span className='material-icons' onClick={() => handleDelete(id)}>delete</span>
                <h3>Routine: <span className='routine-name'>{name}</span></h3>
                <p>Goal: {goal}</p>
                <div className='routine-activities'>
                    <p>Activities: </p>
                    <ul>
                        {activities.length > 0 && activities.map(({name, description, count, duration, routineActivityId}) => {
                            return <li key={routineActivityId}>{name}: {description} | <span className='count'>Count: {count}</span> | <span className='duration'>Duration: {duration}</span></li>
                    })}
                    </ul>
                </div>
            </div>
            <p>Status: {isPublic ? <span>public</span> : <span>private</span>}</p>
            <p>Created by: {creatorName}</p>
        </>
    ) : (
        <>
            <form>
                <h2>Edit Routine:</h2>
                <label>
                    <span>Routine name: </span>
                    <input type='text' name='name' value={form.name} onChange={handleFormChange} />
                </label>
                <label>
                    <span>Routine goal: </span>
                    <input type='text' name='goal' value={form.goal} onChange={handleFormChange} />
                </label>
                <label>
                    <span>Share your routine?</span>
                    <div className='input-radio'>
                        <input type='radio' name='isPublic' value='false' onChange={handleOptionChange} checked={form.isPublic === false} /> Private
                        <input type='radio' name='isPublic' value='true' onChange={handleOptionChange} checked={form.isPublic === true}  /> Public    
                    </div>
                </label>
                {errorMessage && (
                <p className='error-message'>{errorMessage}</p>
                )}
                <input type='submit' value='Create routine' onClick={handleSubmit} />
            </form>
        </>
    )}
}

export default MyRoutinesItem;
