import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser} from '../api';

import {
    MyRoutinesActivitiesForm,
    MyRoutinesForm,
    MyRoutinesItem
} from './index';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);

    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }

    function checkRoutineList(name) {
        const myRoutinesListLowercase = myRoutinesList.map((routine) => {
            const routineName = routine.name.toLowerCase();
            return routineName;
        });
        const nameLowercase = name.toLowerCase();
        return myRoutinesListLowercase.includes(nameLowercase);
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            <div id='my-routines'>
                <MyRoutinesForm fetchData={fetchData} myRoutinesList={myRoutinesList} errorMessage={errorMessage} setErrorMessage={setErrorMessage} checkRoutineList={checkRoutineList} />
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map((routine) => {
                        return <MyRoutinesItem key={routine.id} fetchData={fetchData} checkRoutineList={checkRoutineList} errorMessage={errorMessage} setErrorMessage={setErrorMessage} checkRoutineList={checkRoutineList} {...routine} />
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
