import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser, deleteRoutine} from '../api';

import {
    MyRoutinesActivitiesForm,
    MyRoutinesForm,
    MyRoutinesItem
} from './index';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value});
    }

    const handleDelete = async (id) => {
        try {
            const results = await deleteRoutine(id);
            fetchData();
        } catch (error) {
            console.error(error);
        }
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            <div id='my-routines'>
                <MyRoutinesForm fetchData={fetchData} myRoutinesList={myRoutinesList} errorMessage={errorMessage} handleFormChange={handleFormChange} setErrorMessage={setErrorMessage} />
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map((routine) => {
                        return <MyRoutinesItem key={routine.id} errorMessage={errorMessage} setErrorMessage={setErrorMessage} handleFormChange={handleFormChange} handleDelete={handleDelete} {...routine} />
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
