import React from 'react';
import {Link} from 'react-router-dom';

const Login = ({user}) => {
    const onClickHandler = (event) => {
        event.preventDefault();
    }

    return (
        <>
            <h3>Login</h3>
            {
            !user ? <>
                <form>
                    <label htmlFor='loguser'>username: </label>
                    <input type='text' id='loguser' name='loguser' />
                    <label htmlFor='logpwd'>password: </label>
                    <input type='text' id='logpwd' name='logpwd' />
                    <input type='submit' value='Log in' onClick={onClickHandler} />
                </form>
                <p>Need an account? Click <Link to='/account/register'>here</Link> to register.</p>
            </>
            : <p>You are logged in!</p>
            }
        </>
    );
}

export default Login;
