import React, {useState} from 'react';

import {addActivityToRoutine, updateRoutine} from '../api';
import MyRoutinesActivitiesForm from './MyRoutinesActivitiesForm';

const MyRoutinesItemEdit = ({fetchData, checkRoutineList, name, goal, activities, isPublic, id}) => {
    const initialState = {
        name: name,
        goal: goal,
        isPublic: isPublic
    }

    const [form, setForm] = useState({...initialState})

    const [pendingActivities, setPendingActivities] = useState([]);

    const [routineActivities, setRoutineActivities] = useState(activities);

    const [errorMessage, setErrorMessage] = useState(null);

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value});
    }

    const handleOptionChange = (event) => {
        let isPublicValue;
        if (event.target.value == 'true') {
            isPublicValue = true;
        } else if (event.target.value == 'false') {
            isPublicValue = false;
        }
        setForm({...form, isPublic: isPublicValue});
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage('');
        const {name, goal, isPublic} = form;
        if (checkRoutineList(name) && name !== name) {
            setErrorMessage('This routine already exists! Please choose another name.');
            return;
        }
        try {
            await updateRoutine(name, goal, isPublic, id);
            if (pendingActivities.length > 0) {
                await Promise.all(
                pendingActivities.map(({routineId, activityId, count, duration}) => addActivityToRoutine(routineId, activityId, count, duration))
                );
            }
            setPendingActivities([]);
            setToggle(!toggle);
            fetchData();  
        } catch (error) {
            console.error(error);
        }
    }

    const handleCancel = () => {
        setToggle(!toggle);
        setForm({...initialState});
        fetchData();
    }

    return (
        <form>
            <h3>Edit Routine:</h3>
            <label>
                <span>Routine name: </span>
                <input type='text' name='name' value={form.name} onChange={handleFormChange} />
            </label>
            <label>
                <span>Routine goal: </span>
                <input type='text' name='goal' value={form.goal} onChange={handleFormChange} />
            </label>
            <label>
                <span>Share your routine?</span>
                <div className='input-radio'>
                    <input type='radio' name='isPublic' value='false' onChange={handleOptionChange} checked={form.isPublic === false} /> Private
                    <input type='radio' name='isPublic' value='true' onChange={handleOptionChange} checked={form.isPublic === true}  /> Public    
                </div>
            </label>
            <ul>
            {routineActivities.length > 0 ? routineActivities.map(({name, activityId}) => {
                return (
                    <li key={activityId}>
                        <span className='material-icons' onClick={() => handleDelete(activityId)}>delete</span> {name}
                    </li>
                )
            }) : null}
            </ul>
            <label>
                <span>Add activities to your routine: </span>
                <MyRoutinesActivitiesForm
                    id={id}
                    pendingActivities={pendingActivities}
                    setPendingActivities={setPendingActivities}
                    errorMessage={errorMessage}
                    setErrorMessage={setErrorMessage} />
            </label>
            {errorMessage && (
            <p className='error-message'>{errorMessage}</p>
            )}
            <input type='submit' value='Update routine' onClick={handleSubmit} />
            <input type='button' value='Cancel' onClick={handleCancel} />
        </form>
    )
}

export default MyRoutinesItemEdit;
