import React, { useEffect } from 'react';
import {storeCurrentUser} from '../api';

import {
    Route,
    Switch
} from 'react-router-dom';

import {
    Login,
    Register
} from './index';

import {clearCurrentUser, getCurrentUser} from '../api';

const Account = ({currentUser, setUser}) => {
    const logoutHandler = () => {
        clearCurrentUser();
        setUser({
            username: '',
            loggedIn: false
        });
    }

    useEffect(() => {
        storeCurrentUser(currentUser);
    }, [currentUser])

    return (
        <div className='account'>
        {currentUser.loggedIn ? (
            <>
                <h2>Welcome, {getCurrentUser().username}!</h2>
                <div><a href='#' onClick={logoutHandler}>Log out</a></div>
            </>
        ) : (
            <Switch>
                <Route path='/account/login'>
                    <Login setUser={setUser} currentUser={currentUser} />
                </Route>
                <Route path='/account/register'>
                    <Register setUser={setUser} currentUser={currentUser} />
                </Route>
            </Switch>                
        )}
        </div>
    );
}

export default Account;
