import React, {useState} from 'react';
import {Link} from 'react-router-dom';

const Register = () => {
    const [form, setForm] = useState({
        email: '',
        username: '',
        password: ''
    });

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value.trim()});
    }

    const checkPassword = () => {
        const conditions = ['$', '!', '@', '#'];
        const password = form.password;

        for (i = 0; i < conditions.length; i++) {
            for (j = 0; j < password.length; j++) {
                if (conditions[i] === password[j]) {
                    return true;
                }
            }
        }

        // return conditions.some((condition) => {
        //     password.includes(condition);
        // });
    }

    const onClickHandler = (event) => {
        event.preventDefault();
        console.log('Check Password! ', checkPassword())
    }

    // const checkFields = () => {
    //     const {email, username, password} = form;

    //     return (!email.includes('@')) || (email.length < 6) ? false
    //     : username.length < 6 ? false
    //     : password.length < 8 ? false
    //     : !checkPassword() ? false
    //     : true;
    // }

    // const onClickHandler = (event) => {
    //     event.preventDefault();
    //     if (!checkFields()) {
    //         // error message
    //         console.error('Invalid item!')
    //         return;
    //     } else {
    //         console.log('Form passed!')
    //     }
    // }

    return (
        <>
            <h3>Create an account</h3>
            <form>
                <label htmlFor='email'>
                    <span>email address: </span>
                    <input type='text' value={form.email} name='email' onChange={handleFormChange} />
                </label>
                <label htmlFor='reguser'>
                    <span>username: </span>
                    <input type='text' value={form.username} name='username' onChange={handleFormChange} />
                    <span className='note'>Create a username at least 6 characters long.</span>
                </label>
                <label htmlFor='regpwd'>
                    <span>password: </span>
                    <input type='password' name='password' onChange={handleFormChange} />
                    <span className='note'>Create a password at least 8 characters long and include at least 1 of the following special characters: ( $ ! @ # ).</span>
                </label>
                <input type='submit' value='Submit' onClick={onClickHandler} />
            </form>
            <p>Already have an account? Click <Link to='/account/login'>here</Link> to log in.</p>
        </>
    );
}

export default Register;
