import React, {useState} from 'react';

import {createRoutine} from '../api';

const MyRoutinesForm = ({fetchData, myRoutinesList, errorMessage, setErrorMessage, handleFormChange}) => {
    const [form, setForm] = useState({
        name: '',
        goal: '',
        isPublic: false
    })

    const handleOptionChange = (event) => {
        let isPublicValue;
        if (event.target.value == 'true') {
            isPublicValue = true;
        } else if (event.target.value == 'false') {
            isPublicValue = false;
        }
        setForm({...form, isPublic: isPublicValue});
    }

    function checkRoutineList(name) {
        const myRoutinesListLowercase = myRoutinesList.map((routine) => {
            const routineName = routine.name.toLowerCase();
            return routineName;
        });
        const nameLowercase = name.toLowerCase();
        return myRoutinesListLowercase.includes(nameLowercase);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage('');
        const {name, goal, isPublic} = form;
        if (!name || !goal) {
            setErrorMessage('Both name and goal are required!');
            return;
        }
        if (checkRoutineList(name)) {
            setErrorMessage('This routine already exists! Please choose another name.');
            return;
        }
        try {
            await createRoutine(name, goal, isPublic);
            setForm({name: '', goal: '', isPublic: false});
            fetchData();  
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <form>
            <h2>Create New Routine:</h2>
            <label>
                <span>Routine name: </span>
                <input type='text' name='name' value={form.name} onChange={handleFormChange} />
            </label>
            <label>
                <span>Routine goal: </span>
                <input type='text' name='goal' value={form.goal} onChange={handleFormChange} />
            </label>
            <label>
                <span>Share your routine?</span>
                <div className='input-radio'>
                    <input type='radio' name='isPublic' value='false' onChange={handleOptionChange} checked={form.isPublic === false} /> Private
                    <input type='radio' name='isPublic' value='true' onChange={handleOptionChange} checked={form.isPublic === true}  /> Public    
                </div>
            </label>
            {errorMessage && (
            <p className='error-message'>{errorMessage}</p>
            )}
            <input type='submit' value='Create routine' onClick={handleSubmit} />
        </form>
    )
}

export default MyRoutinesForm;
