import React, { useState, useEffect } from 'react';

import {
    fetchAllActivities,
    checkCurrentUser,
    createActivity
} from '../api';

const Activities = ({currentUser}) => {
    const [activityList, setActivityList] = useState([]);
    const [form, setForm] = useState({
        name: '',
        description: ''
    })
    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchAllActivities();
            setActivityList(results);
        } catch (error) {
            throw error;
        };
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value.trim()});
    }

    function checkActivityList(name) {
        const activityListLowercase = activityList.map((activity) => {
            const activityName = activity.name.toLowerCase();
            return activityName;
        });
        const nameLowercase = name.toLowerCase();
        return activityListLowercase.includes(nameLowercase);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage('');
        const {name, description} = form;
        if (!name || !description) {
            setErrorMessage('Both name and description are required!');
            return;
        }
        if (!checkCurrentUser()) {
            setErrorMessage('You are not logged in!');
            return;
        };
        if (checkActivityList(name)) {
            setErrorMessage('Activity already exists!');
            return;
        }
        try {
            await createActivity(name, description);
            fetchData();
        } catch (error) {
            setErrorMessage(error.message)
            console.error(error);
        }
    }

    return (
        <>
            {currentUser.loggedIn ? (
            <form>
                <h2>Create new activity</h2>
                <label>
                    <span>Activity name: </span>
                    <input type='text' name='name' onChange={handleFormChange} />
                </label>
                <label>
                    <span>Activity description: </span>
                    <textarea name='description' onChange={handleFormChange} />
                </label>
                {errorMessage && (
                    <p className='error-message'>{errorMessage}</p>
                )}
                <input type='submit' value='Create activity' onClick={handleSubmit} />
            </form>
            ) : null
            }
            <div id='activities'>
                <h2>All activities:</h2>
                {activityList.length > 0 && activityList.map(({name, description, id}) => {
                    return <div key={id} className='activity'>
                        <h3>Activity: <span className='activity-name'>{name}</span></h3>
                        <p>Description: {description}</p>
                    </div>
                })}
            </div>
        </>
    );
}

export default Activities;
