import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser, deleteRoutine} from '../api';

import {
    MyRoutinesActivitiesForm,
    MyRoutinesForm,
    MyRoutinesItem
} from './index';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }


    const handleDelete = async (id) => {
        try {
            const results = await deleteRoutine(id);
            fetchData();
        } catch (error) {
            console.error(error);
        }
    }

    const handleEditToggle = (id) => {
        const newList = myRoutinesList.map((routine) => {
            if (routine.id === id) {
                const toggledRoutine = {
                    ...routine,
                    toggled: !routine.toggled
                }
                return toggledRoutine;
            } else {
                return routine;
            }
        })
        setMyRoutinesList(newList);
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            <div id='my-routines'>
                <MyRoutinesForm fetchData={fetchData} myRoutinesList={myRoutinesList} errorMessage={errorMessage} setErrorMessage={setErrorMessage} />
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map((routine) => {
                        return <MyRoutinesItem routine={routine} />
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
