import React, {useEffect, useState} from 'react';
import ReactDOM from 'react-dom';
import {
    BrowserRouter as Router,
    Route,
    Link,
    Switch
} from 'react-router-dom';
import {checkToken, getCurrentUser} from './api';

import {
    Routines,
    MyRoutines,
    Activities,
    Account,
    Home
} from './components';

const App = () => {
    if (getCurrentUser()) {
        const storedUser = getCurrentUser().username;
    }

    const [currentUser, setUser] = useState({
        username: '' || storedUser,
        loggedIn: checkToken ? true : false
    });

    console.log('CURRENT USER => ', currentUser)
    // const storedUser = localStorage.getItem('currentUser');
    // console.log('storedUser => ', storedUser)
    // getCurrentUser().username

    // const fetchData = async () => {
    //     const username = await getCurrentUser().username;
    //     setUser({username: username, loggedIn: true});
    // }

    // useEffect(() => {
    //     if (checkToken()) {
    //         fetchData();
    //     } else {
    //         setUser({username: '', loggedIn: false});
    //     }
    // }, [])

    return (
        <div className='app'>
            <h1>Fitness Trac.kr</h1>
            <Router>
                <nav>
                    <Link to='/'>Home</Link>
                    <Link to='/routines'>Routines</Link>
                    {currentUser.loggedIn ? <Link to='/my-routines'>My Routines</Link> : null}
                    <Link to='/activities'>Activities</Link>
                    <Link to={currentUser.loggedIn ? '/account' : '/account/login'}>Account</Link>                    
                </nav>
                <Switch>
                        <Route path='/routines'>
                            <Routines />
                        </Route>
                        <Route path='/my-routines'>
                            <MyRoutines currentUser={currentUser} />
                        </Route>
                        <Route path='/activities'>
                            <Activities currentUser={currentUser} />
                        </Route>
                        <Route path='/account'>
                            <Account currentUser={currentUser} setUser={setUser} />
                        </Route>
                        <Route path='/'>
                            <Home />
                        </Route>
                    </Switch>
            </Router>
        </div>
    );
}

ReactDOM.render(
    <App />,
    document.getElementById('root')
);
