import React, {useState, useEffect} from 'react';

import {fetchAllActivities} from '../api';

const MyRoutinesActivitiesForm = ({errorMessage, setErrorMessage, id}) => {
    const [form, setForm] = useState({
        name: '',
        goal: '',
        isPublic: false
    })

    const [activityList, setActivityList] = useState([]);

    const [selectedActivity, setSelectedActivity] = useState('');

    const fetchData = async () => {
        try {
            const results = await fetchAllActivities();
            setActivityList(results);
        } catch (error) {
            throw error;
        };
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value});
    }

    const handleOptionChange = (event) => {
        let isPublicValue;
        if (event.target.value == 'true') {
            isPublicValue = true;
        } else if (event.target.value == 'false') {
            isPublicValue = false;
        }
        setForm({...form, isPublic: isPublicValue});
    }

    const handleSelectChange = (event) => {
        const selectedIndex = event.target.options.selectedIndex;
        const activity = event.target.options[selectedIndex].getAttribute('data-key');
        setSelectedActivity(activity);
    }

    const handleAddActivity = (event) => {
        event.preventDefault();
        const activityId = selectedActivity;
        const routineId = routineId;

    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        const {name, goal, isPublic} = form;
        await createRoutine(name, goal, isPublic);
    }

    return (
        <>
            <select name='activities' onChange={handleSelectChange}>
                {activityList ? activityList.map(({id, name}) => {
                    return <option key={id} data-key={id} value={name}>{name}</option>
                }) : null}
            </select>
            {errorMessage && (
                <p className='error-message'>{errorMessage}</p>
            )}
            <button onSubmit={handleAddActivity}>Add to routine</button>
        </>
    )
}

export default MyRoutinesActivitiesForm;
