import React, { useState, useEffect } from 'react';

import {
    fetchAllActivities,
    checkCurrentUser
} from '../api';

const Activities = ({currentUser}) => {
    const [activityList, setActivityList] = useState([]);

    const [form, setForm] = useState({
        name: '',
        description: ''
    })

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value.trim()});
    }

    const fetchData = async () => {
        try {
            const results = await fetchAllActivities();
            setActivityList(results);
        } catch (error) {
            throw error;
        };
    }

    useEffect(() => {
        fetchData();
    }, [])

    return (
        <>
            {currentUser.loggedIn ? (
            <form>
                <h2>Create new activity</h2>
                <input type='text' value={form.name} name='activity-name' onChange={handleFormChange}></input>
            </form>
            ) : null
            }
            <div id='activities'>
                <h2>All activities:</h2>
                {activityList.length > 0 && activityList.map(({name, description, id}) => {
                    return <div key={id} className='activity'>
                        <h3>Activity: {name}</h3>
                        <p>Description: {description}</p>
                    </div>
                })}
            </div>
        </>
    );
}

export default Activities;
