export const BASE_URL = 'https://secret-peak-53848.herokuapp.com';

export async function fetchAllActivities() {
    try {
        const response = await fetch(`${BASE_URL}/api/activities`);
        const data = await response.json();

        return data;
    } catch (error) {
        // throw error;
        console.error(error);
    }
}
