import React, {useState, useEffect} from 'react';

import {fetchAllActivities} from '../api';

const MyRoutinesActivitiesForm = ({pendingActivities, setPendingActivities, errorMessage, setErrorMessage, id}) => {
    const [activityList, setActivityList] = useState([]);

    const [selectedActivity, setSelectedActivity] = useState({
        routineId: id,
        activityId: 1,
        count: 1,
        duration: 1,
        name: ''
    });

    const fetchData = async () => {
        try {
            const results = await fetchAllActivities();
            setActivityList(results);
        } catch (error) {
            throw error;
        };
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleSelectChange = (event) => {
        const selectedName = event.target.value;
        const selectedIndex = event.target.options.selectedIndex;
        const activityId = Number(event.target.options[selectedIndex].getAttribute('data-key'));
        setSelectedActivity({...selectedActivity, activityId: activityId, name: selectedName});
    }

    const handleNumberChange = (event) => {
        setSelectedActivity({...selectedActivity, [event.target.name]: Number(event.target.value)});
    }

    const getActivityName = () => {
        const [activity] = activityList.filter(activity => selectedActivity.activityId === activity.id);
        return activity.name;
    }

    const pushToStateArray = (newName, newData) => {
        const newNamesArray = pendingActivities.names.slice();
        const newDataArray = pendingActivities.data.slice();
        newNamesArray.push(newName);
        newDataArray.push(newData);
        return {...pendingActivities, names: newNamesArray, data: newDataArray};
    }

    const handleAddActivity = (event) => {
        event.preventDefault();
        const newObject = pushToStateArray(getActivityName(), selectedActivity);
        setPendingActivities(newObject);
    }

    const handleDelete = (index) => {
        console.log(index)
        // need to combine pendingActivity names and data
    }

    return (
        <>
            <select name='activities' onChange={handleSelectChange}>
                {activityList ? activityList.map(({id, name}) => {
                    return <option key={id} data-key={id} value={name}>{name}</option>
                }) : null}
            </select>
            {errorMessage && (
                <p className='error-message'>{errorMessage}</p>
            )}
            <label>
                <span>Count: </span>
                <input type='number' name='count' value={selectedActivity.count} onChange={handleNumberChange} />
            </label>
            <label>
                <span>Duration (minutes): </span>
                <input type='number' name='duration' value={selectedActivity.duration} onChange={handleNumberChange} />
            </label>
            <button onClick={handleAddActivity}>Add activity</button>
            <ul>
                {pendingActivities.names ? pendingActivities.names.map((name, index) => {
                    return (
                        <li key={index}>
                            <span className='material-icons' onClick={() => handleDelete(index)}>delete</span> {name}
                        </li>
                    )
                }) : null}
            </ul>
        </>
    )
}

export default MyRoutinesActivitiesForm;
