import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser, createRoutine, deleteRoutine} from '../api';

import {
    MyRoutinesActivitiesForm,
    MyRoutinesForm
} from './index';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);
    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleSubmit = async (event) => {
        event.preventDefault();
        const {name, goal, isPublic} = form;
        try {
            await createRoutine(name, goal, isPublic);
            fetchData();  
        } catch (error) {
            console.error(error);
        }
    }

    // const handleEditToggle = (id) => {
    //     const newList = myRoutinesList.map((routine) => {
    //         if (routine.id === id) {
    //             const toggledRoutine = {
    //                 ...routine,
    //                 isComplete: !routine.isComplete
    //             }
    //             return toggledRoutine;
    //         } else {
    //             return routine;
    //         }
    //     })
    // }

    const handleDelete = async (id) => {
        try {
            const results = await deleteRoutine(id);
            fetchData();
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <>
            <div id='my-routines'>
                <MyRoutinesForm form={form} errorMessage={errorMessage} />
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map(({name, goal, creatorName, activities, isPublic, id}) => {
                        return <div key={id} className='routine'>
                        <span className='material-icons' onClick={() => handleEditToggle(id)}>edit</span>
                        <span className='material-icons' onClick={() => handleDelete(id)}>delete</span>
                        <h3>Routine: <span className='routine-name'>{name}</span></h3>
                        <p>Goal: {goal}</p>
                        <div className='routine-activities'>
                            <p>Activities: </p>
                            <ul>
                                {activities.length > 0 && activities.map(({name, description, count, duration, routineActivityId}) => {
                                    return <li key={routineActivityId}>{name}: {description} | <span className='count'>Count: {count}</span> | <span className='duration'>Duration: {duration}</span></li>
                                })}
                            </ul>
                        </div>
                        <p>Status: {isPublic ? <span>public</span> : <span>private</span>}</p>
                        <p>Created by: {creatorName}</p>
                    </div>
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
