import React, {useState} from 'react';

import {addActivityToRoutine, updateRoutine, deleteRoutine} from '../api';
import MyRoutinesActivitiesForm from './MyRoutinesActivitiesForm';

const MyRoutinesItem = ({fetchData, checkRoutineList, errorMessage, setErrorMessage, name, goal, creatorName, activities, isPublic, id}) => {
    const initialState = {
        name: name,
        goal: goal,
        isPublic: isPublic
    }

    const [form, setForm] = useState({
        ...initialState
    })

    const [toggle, setToggle] = useState(false)

    const [pendingActivities, setPendingActivities] = useState({
        data: [],
        names: []
    });

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value});
    }

    const handleOptionChange = (event) => {
        let isPublicValue;
        if (event.target.value == 'true') {
            isPublicValue = true;
        } else if (event.target.value == 'false') {
            isPublicValue = false;
        }
        setForm({...form, isPublic: isPublicValue});
    }

    const handleEditToggle = () => {
        setToggle(!toggle);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();
        setErrorMessage('');
        const {name, goal, isPublic} = form;
        if (checkRoutineList(name) && name !== name) {
            setErrorMessage('This routine already exists! Please choose another name.');
            return;
        }
        try {
            await updateRoutine(name, goal, isPublic, id);
            console.log('WORKS 3')

            if (pendingActivities.data) {
                await Promise.all(
                pendingActivities.data.map(({routineId, activityId, count, duration}) => addActivityToRoutine(routineId, activityId, count, duration))
                );
            }
            setPendingActivities({...pendingActivities, data: [], names: []});
            fetchData();  
        } catch (error) {
            console.error(error);
        }
    }

    const handleCancel = () => {
        setToggle(!toggle);
        setForm({...initialState});
        fetchData();
    }

    const handleDelete = async (id) => {
        try {
            const results = await deleteRoutine(id);
            fetchData();
        } catch (error) {
            console.error(error);
        }
    }

    return (
        <>
        {!toggle ? (
            <div key={id} className='routine'>
                <span className='material-icons' onClick={handleEditToggle}>edit</span>
                <span className='material-icons' onClick={() => handleDelete(id)}>delete</span>
                <h3>Routine: <span className='routine-name'>{name}</span></h3>
                <p>Goal: {goal}</p>
                <div className='routine-activities'>
                    <p>Activities: </p>
                    <ul>
                        {activities.length > 0 && activities.map(({name, description, count, duration, routineActivityId}) => {
                            return <li key={routineActivityId}>{name}: {description} | <span className='count'>Count: {count}</span> | <span className='duration'>Duration: {duration}</span></li>
                        })}
                    </ul>
                </div>
                <p>Status: {isPublic ? <span>public</span> : <span>private</span>}</p>
                <p>Created by: {creatorName}</p>
            </div>
        ) : (
            <form>
                <h3>Edit Routine:</h3>
                <label>
                    <span>Routine name: </span>
                    <input type='text' name='name' value={form.name} onChange={handleFormChange} />
                </label>
                <label>
                    <span>Routine goal: </span>
                    <input type='text' name='goal' value={form.goal} onChange={handleFormChange} />
                </label>
                <label>
                    <span>Share your routine?</span>
                    <div className='input-radio'>
                        <input type='radio' name='isPublic' value='false' onChange={handleOptionChange} checked={form.isPublic === false} /> Private
                        <input type='radio' name='isPublic' value='true' onChange={handleOptionChange} checked={form.isPublic === true}  /> Public    
                    </div>
                </label>
                <label>
                    <span>Add activities to your routine: </span>
                    <MyRoutinesActivitiesForm
                        id={id}
                        pendingActivities={pendingActivities}
                        setPendingActivities={setPendingActivities}
                        errorMessage={errorMessage}
                        setErrorMessage={setErrorMessage} />
                </label>
                {errorMessage && (
                <p className='error-message'>{errorMessage}</p>
                )}
                <input type='submit' value='Update routine' onClick={handleSubmit} />
                <input type='button' value='Cancel' onClick={handleCancel} />
            </form>
        )}
        </>
    )
}

export default MyRoutinesItem;
