import React, {useState, useEffect} from 'react';

import {fetchRoutinesByUser} from '../api';

const MyRoutines = ({currentUser}) => {
    const [myRoutinesList, setMyRoutinesList] = useState([]);
    const [form, setForm] = useState({
        name: '',
        goal: '',
        isPublic: false
    })
    const [errorMessage, setErrorMessage] = useState(null);

    const fetchData = async () => {
        try {
            const results = await fetchRoutinesByUser(currentUser.username);
            setMyRoutinesList(results);
        } catch (error) {
            console.error(error);
        }
    }

    useEffect(() => {
        fetchData();
    }, [])

    const handleFormChange = (event) => {
        setForm({...form, [event.target.name]: event.target.value.trim()});
    }

    const handleSubmit = (event) => {
        event.preventDefault();
    }

    return (
        <>
            <div id='my-routines'>
                <form>
                    <h2>Create New Routine:</h2>
                    <label>
                        <span>Routine name: </span>
                        <input type='text' name='name' value={form.name} onChange={handleFormChange} />
                    </label>
                    <label>
                        <span>Routine goal: </span>
                        <input type='text' name='goal' value={form.goal} onChange={handleFormChange} />
                    </label>
                    <label>
                        <span>Share your routine?</span>
                        <input type='radio' name='is-public' value='true' onChange={handleFormChange} checked>Private</input>
                        <input type='radio' name='is-public' value='false' onChange={handleFormChange}>Public</input>
                    </label>
                    {errorMessage && (
                    <p className='error-message'>{errorMessage}</p>
                    )}
                    <input type='submit' value='Create routine' onClick={handleSubmit} />
                </form>
                <h2>My Routines:</h2>
                <ul>
                    {myRoutinesList.length > 0 && myRoutinesList.map(({name, goal, creatorName, activities, id}) => {
                        return <div key={id} className='routine'>
                        <h3>Routine: <span className='routine-name'>{name}</span></h3>
                        <p>{goal}</p>
                        <div className='routine-activities'>
                            <p>Activities: </p>
                            <ul>
                                {activities.length > 0 && activities.map(({name, description, count, duration, routineActivityId}) => {
                                    return <li key={routineActivityId}>{name}: {description} | <span className='count'>Count: {count}</span> | <span className='duration'>Duration: {duration}</span></li>
                                })}
                            </ul>
                        </div>
                        <p>Created by: {creatorName}</p>
                    </div>
                    })}
                </ul>
            </div>
        </>
    )
}

export default MyRoutines;
